$(document).ready(function(){$('form#delete-form input[name="delete"]').click(function(c){c.preventDefault();var b=$(this).attr("class");var a=$(this).parent().parent();if(confirm("Are you sure you want to delete this device?")){$.ajax({type:"POST",url:"delete_device.php",data:{ID:b},cache:false,success:function(d){$("#update-message span").text(d);a.fadeOut("slow",function(){$(this).remove();});}});}});$('form#delete-form input[name="update"]').click(function(g){g.preventDefault();var f=$(this).attr("class");var a=$("form#delete-form").find('input[name="location"].'+f).val();var d=$("form#delete-form").find('input[name="type"].'+f).val();var c=$("form#delete-form").find('input[name="ip_address"].'+f).val();var b=$(this).parent().parent();$.ajax({type:"POST",url:"update_device.php",data:{ID:f,DeviceLocation:a,Type:d,IPAddress:c},cache:false,success:function(e){$("#update-message span").text(e);}});});});